    const byteArray = new Uint8Array(byteNumbers);
    let objectUrl = URL.createObjectURL(new Blob([byteArray], {
      type: "application/pdf"
    }));
    let url = this.sanitizer.bypassSecurityTrustResourceUrl(objectUrl);
    this.pdfurl = url;
  }

}

