  download() {
    var _this = this;

    return _asyncToGenerator(function* () {
      let showBible = _this.pathList.some(path => {
        return path.includes('Build Delivery Bible');
      });

      if (showBible) {
        _this._common.$downloadBible.next(true);

        return;
      }

      let downloadFolderList = _this.filteredFolderList.filter(res => res.isSelected === true);

      let downloadFileList = _this.filteredFileList.filter(res => res.isSelected === true);

      let folderPathList = [];
      let filePathList = [];
      downloadFolderList.map(res => folderPathList.push(res.path.slice(13) + '/' + res.name));
      downloadFileList.map(res => filePathList.push(res.path.slice(13) + '/' + res.name));
      let downloadPathList = [...folderPathList, ...filePathList];

      if (downloadPathList.length <= 0) {
        _this._modal.showMsg("No File Selected To Download", "Error!", "error");

        return;
      }

      _this._directory.download(downloadPathList, _this.base_path).subscribe(res => {
        _this.unselectAllFoldersAndFiles();

        _this.unselectAllGFolders();

        if (res.type === HttpEventType.DownloadProgress) {
          _this._common.showDownloadModal = true;

          _this._ngxService.stop();

          const percentDone = Math.round(100 * res.loaded / res.total);
          _this._common.downloadedData = res.loaded;
          _this._common.totalDownloadFileSize = res.total;
          _this._common.downloadProgress = percentDone;
        }

        if (res.type === HttpEventType.Response) {
          _this._common.downloadComplete = true;

          _this._FileSaverService.save(res.body, "DocuExpertDoc");
        }
      });
    })();
  } // ***********************************For Selecting And Downloading Files*******************************
