    } else {
      transferArrayItem(
        event.previousContainer.data,
        event.container.data,
        event.previousIndex,
        event.currentIndex,
      );
    }
    console.log(event.container.data);

    event.container.data.forEach((res: any, index: number) => {
      if (!this.docMergePdf.includes(res)) {
